authFunction:
function authFunction(uname, password, $http, $rootScope, success, error) {

    var authSuccess = success;
    var authError = error;

    if (window.device) {
        var networkState = navigator.connection.type;
        if (networkState == "none" || networkState == "unknown") {
            var backToPrevPage = function() {
                //$window.history.back();
            };
            navigator.notification.alert("Oops! You are not connected to Internet. Please check your settings and try again",
                backToPrevPage, 'No Network', 'Ok');
            return;
        }
    }
    if (!$rootScope.isAppAuthorizedReplaced) {
        //alert ("Called 1");
        var isAppAuth = MyCampusApp.isAppAuthorized;
        MyCampusApp.isAppAuthorized = function(app, rs) {
            //alert ("called" + app + " loggedin : " + rs.loggedin);
            if (rs.loggedin && app.name == "myWNC2") {
                return false;
            } else {
                return isAppAuth(app, rs);
            }
        }
        $rootScope.isAppAuthorizedReplaced == true;
    }

  	var sessionExpired = function() {
        $rootScope.ticket = null;
        $rootScope.loggedin = false;
        $rootScope.userroles = null;
        $.jStorage.deleteKey('username');
        $.jStorage.deleteKey('password');
        $.jStorage.deleteKey('ticket');
        $.jStorage.deleteKey('userroles');
        $rootScope.setRoute("/login");
      	var dofocus = function() {
        	$("#loginUsername").val(uname);
          	$("#loginPassword").focus();
      	}
        if (window.device) {
            navigator.notification.alert("Please login again.", dofocus, 'Session Timeout', 'OK');
        } else {
            apprise("Please login again.", {
                'verify': false,
                'textYes': "Ok"
            }, function(r) {
                dofocus();
            });
        }
    }
    //Banner Authentication with LDAP - middlware auth

    var url = "https://kryptosmw.kryptosmobile.com/webadapter3/services/authenticate/CSN/PS";
    var data = {};
    if (window.device) {
        data = "username=" + uname + "&password=" + password;
    } else {
        url = "/websimulator/json?url=" + url;
        data = {
            method: "POST",
            body: "username=" + uname + "&password=" + password
        };
    }
    var authUrl = url;
    var authData = data;
    $rootScope.generateST = function(tgt, service, callback) {
        //Bypassing the ST as there is no service ticket required here..
        callback(tgt);
    };

    $rootScope.postAPI = function(endpoint, postdata, callback) {
        if (window.device) {
            var networkState = navigator.connection.type;
            if (networkState == "none" || networkState == "unknown") {
                var backToPrevPage = function() {
                    //$window.history.back();
                };
                navigator.notification.alert("Oops! You are not connected to Internet. Please check your settings and try again",
                    backToPrevPage, 'No Network', 'Ok');
                return;
            }
        }
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "POST";
            var proxyData = postdata;
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + serviceurl;
                proxyMethod = "POST";
                proxyData = {
                    method: "POST",
                    body: postdata
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                  	//sessionExpired();                  	
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        data.roles = ["student"];
                        $rootScope.postAPI(endpoint, postdata, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };

    $rootScope.getAPI = function(endpoint, callback) {
        if (window.device) {
            var networkState = navigator.connection.type;
            if (networkState == "none" || networkState == "unknown") {
                var backToPrevPage = function() {
                    //$window.history.back();
                };
                navigator.notification.alert("Oops! You are not connected to Internet. Please check your settings and try again",
                    backToPrevPage, 'No Network', 'Ok');
                return;
            }
        }
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                  	//sessionExpired();
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        data.roles = ["student"];
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };




    $.blockUI({
        message: '<div style="margin: 2px; vertical-align: middle; display: inline-block"><i class="icon-cog icon-spin icon-4x"></i><h3 style="color:#fff;">Authenticating</h3></div>'
    });

    $http.post(url, data).success(function(data, status, headers, config) {
        data.roles = ["student"];
        success(data);
        $.unblockUI();
    }).error(function(data, status, headers, config) {
        error(data);
        $.unblockUI();
    });
    /*Added by Nick- for Login go button - start*/
    $('#loginForm').on('submit', function(event) {
        event.preventDefault();
        $scope.login();
        $scope.$digest();
        return false;
    });
    /*end*/
}

pages.pageprocessor:
function pageprocessorPSHolds(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $.blockUI();
    var url = $rootScope.middlewareServerUrl + pagedef.endpoint;
    $rootScope.postAPI(url, "", function(data) {
        $scope.holds = data.holds;       $.unblockUI();

        if (data.holds.length == 0) {
            var gotoHome = function() {
                $rootScope.$apply(function() {
                    $location.path("/home");
                });
            };
            if (window.device) {
                navigator.notification.alert("You have no hold records", gotoHome, 'Alert', 'Ok');
            } else {
                apprise("You have no hold records", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {
                    gotoHome();
                });
            }
        }
    });
    $scope.loadInst = function(index) {
        var hold = $scope.holds[index];
      	var url = $rootScope.middlewareServerUrl + "/psholdinstructions";
        $rootScope.postAPI(url, "holdid=SRVC_LINK$" + index, function(data) {
			$scope.holds[index].inst = data.hold;
          	$scope.holds[index].instructions = data.hold[0].INSTRUCTIONS;
        });

    }
}

function pageprocessorPSGrades(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
