    $scope.classschedulenoterm = function() {
        $scope.psclassschedule = null;
        var url = $rootScope.middlewareServerUrl + "/psclassschedule";
        $rootScope.postAPI(url, "", function(data) {
            $scope.psclassschedule = data.psclassschedule;
            $.unblockUI();
        });
    };
    $rootScope.postAPI(url, "", function(data) {
        $scope.terms = data.terms;

        $.unblockUI();
        if($scope.terms.length == 0) {
            $scope.classschedulenoterm();
        }else {
         $scope.showterm=true; 
        }
    });
}

function pageprocessorPSAccountSummaryPage5(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.curstep = 1;
    $scope.showerrmsg = false;
    $scope.errmessage = "";
    $scope.acknowledge = false;
    var url = $rootScope.middlewareServerUrl + pagedef.endpoint;
    $rootScope.postAPI(url, "", function(data) {
        $scope.paymentplans = data.paymentplans;
        $scope.paymentflag = data.paymentflag;
        $.unblockUI();
    });
    $scope.s2back = function() {
        $scope.curstep = 1;
    }
    $scope.s3cancel = function() {
        $scope.curstep = 1;
    }
    $scope.selectplan = function(plan) {
        $rootScope.seletedplan = plan;
        var url = $rootScope.middlewareServerUrl + "/psppreviewinstallments";
        $rootScope.postAPI(url, "planId=" + $rootScope.seletedplan.PLAN_ID, function(data) {
            $scope.installmentdetails = data.installmentdetails;
            $scope.installmentschedule = data.installmentschedule
            $scope.curstep = 2;
            $.unblockUI();
        });
    }
    $scope.showagreement = function() {
        var url = $rootScope.middlewareServerUrl + "/psppagreement";
        $rootScope.postAPI(url, "planId=" + $rootScope.seletedplan.PLAN_ID, function(data) {
            var paymentflag = data.paymentflag;
            if (paymentflag.length > 0) {
                $scope.showerrmsg = true;
              if(paymentflag[0].MESSAGE && paymentflag[0].MESSAGE.indexOf("Your Elected Amount is less than the minimum allowed for this contract.") != -1) {
                $scope.errmessage = "Account balance isn’t high enough to make you eligible for a payment plan";
              }else {
                $scope.errmessage = paymentflag[0].MESSAGE;
              }
            } else {
                $scope.curstep = 3;
                $rootScope.agreementdetails = data.agreementdetails;
                $rootScope.installmentschedule = data.installmentschedule
                    //$location.path("/app/PaymentPlan/PaymentPlanPage3");
            }
            $.unblockUI();
        });
    };
    $scope.enroll = function() {
        if ($scope.acknowledge == false) {
            if (window.device) {
                navigator.notification.alert("You must click on the Agreement box below before you can enroll in this plan.",
                    null, 'Agreement', 'Ok');
            } else {
                apprise("You must click on the Agreement box below before you can enroll in this plan.", {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {

                });
            }
        } else {
            var url = $rootScope.middlewareServerUrl + "/psppenroll";
            $rootScope.postAPI(url, "planId=" + $rootScope.seletedplan.PLAN_ID, function(data) {
                $scope.curstep = 4;
                $scope.result = data.result;
                $scope.installmentschedule = data.installmentschedule
                $.unblockUI();
            });
        }
    };
    $scope.pay = function() {
        $scope.curstep = 5;
        if (window.device) {
            navigator.notification.confirm(
                'Please visit my.wnc.edu to pay for your classes.', // message
                openWeb, // callback to invoke with index of button pressed
                'Payment', // title
                ['Cancel', 'Visit Website'] // buttonLabels
            );
        } else {
            apprise("Please visit my.wnc.edu to pay for your classes.", {
                'verify': true,
                'textYes': "Visit Website",
                'textNo': "Cancel"
            }, function(r) {
                if (r) {
                    openWeb(2);
                } else {
                    MyCampusApp.modalDialogDisplayed = false;
                }
            });
        }

        function openWeb(buttonIndex) {
            var launchparams = "location=yes,hidden=no;enableViewportScale=yes,toolbarposition=top,closebuttoncaption=Back";
            if (window.device && window.device.platform == 'Android') {
                launchparams = "location=yes,hidden=no";
            }

            if (buttonIndex == 2) {
                var iabRef = window.open("http://my.wnc.edu", "_blank", launchparams);
                var hideBlockUi = function() {
                    iabRef.show();
                    $.unblockUI();
                };
                var iabClose = function(data) {
                    $.unblockUI();
                    window.location.href = "index.html#home";

                };
                iabRef.addEventListener("exit", iabClose);
                var loadStop = function(data) {
                    //hideBlockUi();

                };
                iabRef.addEventListener("loadstop", loadStop);
            } else {
                window.location.href = "index.html#home";
            }
        }
    };

    $(".app-content").prepend($compile(pagedef.pageTemplate)($scope));
}

