function pageprocessorEnrollPage2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    $scope.showdrop = false;
    $scope.showcart = false;
    $scope.showadd = false;
    
    if ($rootScope.fromPage == "EnrollPage6") {
        $routeParams.termId = $rootScope.termId;
        $routeParams.type = $rootScope.actiontype;
        $rootScope.fromPage = "";
    }

    if ($routeParams.termId) {
        var termid = $routeParams.termId;
        $rootScope.termid = termid;
        $scope.termname = function() {
            var termnm = "";
            angular.forEach($rootScope.enrollterms, function(value, key) {
                if (termid == value.TERM_ID) {
                    termnm = value.TERM_NAME + " " + value.CAREER;
                }
            });
            return termnm;
        }
        $rootScope.termname = $scope.termname();
    }
    if ($routeParams.type == "Add") {
        $scope.showadd = true;
        var termnm = "";
        angular.forEach($rootScope.enrollterms, function(value, key) {
            if ($routeParams.termId == value.TERM_ID) {
                termnm = value.TERM_NAME + " " + value.CAREER;
            }
        });
        $rootScope.termname = termnm;
        $scope.addToCart = function() {
            var classNumID = $scope.classid;
            if (!$scope.classid) {
                if (window.device) {
                    navigator.notification.alert("You should enter a class number first !");
                } else {
                    alert("You should enter a class number first !");
                }
                return;
            }           
            var url = $rootScope.middlewareServerUrl + "/psenrollmentaddclasstocart";
            var postdata = "termId=" + $rootScope.termid + "&classNumberButtonId=" + $rootScope.buttonid[0].CLASS_NUMBER_BUTTON_ID + "&classNumber=" + classNumID;
            $rootScope.postAPI(url, postdata, function(data) {
                if (window.device) {
                    navigator.notification.alert(data.message[0].MESSAGE);
                } else {
                    alert(data.message[0].MESSAGE);
                }
            });
        }

    } else if ($routeParams.type == "Drop") {
        $scope.showdrop = true;
        $scope.loadClasstoDrop = function() {
            var url = $rootScope.middlewareServerUrl + "/psclassestodrop";
            $rootScope.postAPI(url, "termId=" + $routeParams.termId, function(data) {
                $scope.classestodrop = data.classestodrop;
            });
        }
        $scope.loadClasstoDrop();
        $scope.execDrop = function(classid) {
            var url = $rootScope.middlewareServerUrl + "/psdropclass";
            $rootScope.postAPI(url, "termId=" + $routeParams.termId + "&classIdToDrop=" + classid, function(data) {
                if (data.dropresult[0].STATUS == "Success") {
                    $scope.loadClasstoDrop();
                    navigator.notification.alert(data.dropresult[0].CLASS + " has been removed from your schedule");
                } else if (data.dropresult[0].MESSAGE) {
                    if (window.device) {
                        navigator.notification.alert(data.dropresult[0].MESSAGE);
                    } else {
                        alert(data.dropresult[0].MESSAGE);
                    }
                }
            });

        }
        $scope.dropClass = function(classid) {
            var onConfirm = function(buttonIndex) {
                if (buttonIndex == 1) {
                    $scope.execDrop(classid);
                }
            }
            if (window.device) {
                navigator.notification.confirm(
                    'Are you sure you want to drop.?', // message
                    onConfirm, // callback to invoke with index of button pressed
                    'Just Confirming', // title
                    ['Yes', 'No'] // buttonLabels
                );
            } else {
                apprise("Are you sure you want to drop.?", {
                    'verify': true,
                    'textYes': "Yes",
                    'textNo': "No"
                }, function(r) {
                    if (r) {
                        onConfirm(1);
                    }
                });
            }
        }

    } else if ($routeParams.type == "Cart") {
        $scope.showcart = true;
        $scope.loadCart = function() {
            var url = $rootScope.middlewareServerUrl + "/psenrollmentshoppingcartforterm";
            $rootScope.postAPI(url, "termId=" + $routeParams.termId, function(data) {
                $scope.enrollmentcart = data.enrollmentcart;
            });
        }
        $scope.loadCart();
        $scope.execRemoveFromCart = function(classid, classnm) {
            var url = $rootScope.middlewareServerUrl + "/psenrollmentremoveclassfromcart";
            $rootScope.postAPI(url, "termId=" + $routeParams.termId + "&classNumberIdToRemove=" + classid, function(data) {
                $scope.loadCart();
                navigator.notification.alert(decodeURI(classnm) + "is removed from the shopping cart.");

            });
        }
        $scope.removeFromCart = function(classid, classnm) {
            var onConfirm = function(buttonIndex) {
                if (buttonIndex == 1) {
                    $scope.execRemoveFromCart(classid, classnm);
                }
            }
            if (window.device) {
                navigator.notification.confirm(
                    'Are you sure you want to Remove?', // message
                    onConfirm, // callback to invoke with index of button pressed
                    'Just Confirming', // title
                    ['Yes', 'No'] // buttonLabels
                );
            } else {
                apprise("Are you sure you want to Remove.?", {
                    'verify': true,
                    'textYes': "Yes",
                    'textNo': "No"
                }, function(r) {
                    if (r) {
                        onConfirm(1);
                    }
                });
            }
        }


        $scope.execSubmitCart = function(classid, classnm) {
            var url = $rootScope.middlewareServerUrl + "/psfinishenrollment";
            var vdata = "termId=" + $routeParams.termId + "&submitCartButtonId=" + $rootScope.buttonid[0].SUBMIT_CART_BUTTON_ID + "&finishEnrollmentButtonId=DERIVED_REGFRM1_SSR_PB_SUBMIT";
            $rootScope.postAPI(url, vdata , function(data) {
                console.log("enrollment results:");
                console.log(data);
                //$scope.loadCart();
                $rootScope.enrollmentres = data.enrollmentresult;
                $rootScope.termId = $routeParams.termId;
                $rootScope.actiontype=$routeParams.type;
                $location.path("/app/Enroll/EnrollPage6");
                return;
            });
        }
        $scope.submitCart = function() {
            var onConfirm = function(buttonIndex) {
                if (buttonIndex == 1) {
                    $scope.execSubmitCart();
                }
            }
            if (window.device) {
                navigator.notification.confirm(
                    'Are you sure you want to Submit.?', // message
                    onConfirm, // callback to invoke with index of button pressed
                    'Just Confirming', // title
                    ['Yes', 'No'] // buttonLabels
                );
            } else {
                apprise("Are you sure you want to Submit.?", {
                    'verify': true,
                    'textYes': "Yes",
                    'textNo': "No"
                }, function(r) {
                    if (r) {
                        onConfirm(1);
                    }
                });
            }
        }

    }
}

